styles:
/* Main window */
QMainWindow {
  background-color: #1a1a1a;
}

/* Menu bar */
QMenuBar {
  background-color: #2d2d2d;
  color: #ffffff;
}
QMenuBar::item:selected {
  background-color: #3d3d3d;
}
QMenu {
  background-color: #2d2d2d;
  color: #ffffff;
  border: 1px solid #3d3d3d;
}
QMenu::item:selected {
  background-color: #3d3d3d;
}

/* Toolbar */
QToolBar {
  background-color: #2d2d2d;
  border-bottom: 1px solid #3d3d3d;
}
QToolButton {
  background-color: transparent;
  border: none;
  padding: 4px;
}
QToolButton:hover {
  background-color: #3d3d3d;
}

/* Video player */
VideoPlayer {
  background-color: #1a1a1a;
}
VideoPlayer QFrame {
  background-color: #000000;
  border: 1px solid #3d3d3d;
}
VideoPlayer QPushButton {
  background-color: #2d2d2d;
  border: none;
  padding: 6px;
  border-radius: 4px;
}
VideoPlayer QPushButton:hover {
  background-color: #3d3d3d;
}
VideoPlayer QSlider::groove:horizontal {
  background: #2d2d2d;
  height: 4px;
}
VideoPlayer QSlider::handle:horizontal {
  background: #4a9eff;
  width: 12px;
  margin: -4px 0;
  border-radius: 6px;
}

/* Analysis panel */
QDockWidget {
  titlebar-close-icon: url(close.png);
  titlebar-normal-icon: url(float.png);
}
QDockWidget::title {
  background-color: #2d2d2d;
  padding: 6px;
  color: #ffffff;
}
AnalysisPanel {
  background-color: #2d2d2d;
}
AnalysisPanel QTabWidget::pane {
  border: 1px solid #3d3d3d;
}
AnalysisPanel QTabBar::tab {
  background-color: #1a1a1a;
  color: #ffffff;
  padding: 8px 16px;
  border: none;
}
AnalysisPanel QTabBar::tab:selected {
  background-color: #2d2d2d;
}
AnalysisPanel QComboBox {
  background-color: #1a1a1a;
  color: #ffffff;
  border: 1px solid #3d3d3d;
  padding: 4px;
}
AnalysisPanel QSpinBox {
  background-color: #1a1a1a;
  color: #ffffff;
  border: 1px solid #3d3d3d;
  padding: 4px;
}
AnalysisPanel QPushButton {
  background-color: #4a9eff;
  color: #ffffff;
  border: none;
  padding: 8px 16px;
  border-radius: 4px;
}
AnalysisPanel QPushButton:hover {
  background-color: #3d8ee6;
}

/* Status bar */
QStatusBar {
  background-color: #2d2d2d;
  color: #ffffff;
}
QStatusBar::item {
  border: none;
}
